mapper.select:
    select 

    from bill_biz_doubt



				and bill_date between #{startDate} and #{endDate}


				and bill_date >= #{startDate}


				and bill_date <= #{endDate}



			and batch_no = #{batchNo}


			and bill_type = #{billType}


			and pay_channel = #{payChannel}


			and batch_no = #{batchNo}


			and merchant_app_code = #{merchantAppCode}


			and pay_merch_order_no = #{payMerchOrderNo}


			and refund_merch_order_no = #{refundMerchOrderNo}


			and pay_order_no = #{payOrderNo}


			and refund_order_no = #{refundOrderNo}


			and pay_trade_no = #{payTradeNo}


			and refund_trade_no = #{refundTradeNo}
