mapper.select:
select 

from bill_biz_file_notify
where merchant_app_code = #{merchantAppCode,jdbcType=VARCHAR}
  and bill_date = #{billDate,jdbcType=DATE}

mapper.update:
update bill_biz_file_notify
set 

   file_path = #{filePath,jdbcType=VARCHAR},

  file_status = #{fileStatus,jdbcType=VARCHAR},
  file_remark = #{fileRemark,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

update bill_biz_file_notify
set notify_url = #{notifyUrl,jdbcType=VARCHAR},
  notify_status = #{notifyStatus,jdbcType=VARCHAR},
  notify_remark = #{notifyRemark,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
