mapper.update:
update bill_biz_summary
set batch_run_success_count = #{batchRunSuccessCount,jdbcType=INTEGER}
where id = #{summaryId,jdbcType=BIGINT}

update bill_biz_summary
set bill_make_success_count = #{billMakeSuccessCount,jdbcType=INTEGER}
where id = #{summaryId,jdbcType=BIGINT}

update bill_biz_summary
set download_notify_success_count = #{downloadNotifySuccessCount,jdbcType=INTEGER}
where id = #{summaryId,jdbcType=BIGINT}

mapper.select:
    select 

    from bill_biz_summary



				and bill_date between #{startDate} and #{endDate}


				and bill_date >= #{startDate}


				and bill_date <= #{endDate}
